import React, {useEffect, useState} from 'react';
import Circle from './Circle/index';
import ReactDOM from 'react-dom';
import { objectTypeCallProperty } from '@babel/types';


function returnCourses(courses) {
    let container = <div></div>;
    var circles = [];

    courses.forEach((course) => {
        circles.push(<Circle title={course.title} code={course.code} description={course.description} />);
    });

    return <div style={{
        display: "flex"
    }}>{circles}</div>;
};

// since the entire thing has apparently been converted to string:
// we iterate it over similarly to an array of chars, using { and } as signifiers of the beginning and end of an object. 
// from there we might be able to use JSON.parse

function parseCourses(data) {
    const text = String(data);
    console.log(text);
    // return an array of JSON objects
    var courseObjects = [];

    var inObj = false;
    var objtext = "";
    for (let i = 0; i++; i < text.length) {
        if ((text[i] === "[") || (text[i] === "]")) {
            continue;
        }
        if ((text[i] === "{") && (!inObj)) {
            inObj = true;
            objtext = objtext.concat(text[i]);
        } else if ((text[i] === ",") && (inObj) && (text[i] === "}")) {
            const obj = JSON.parse(objtext);
            courseObjects.push(obj);
            inObj = false;
        } else if (inObj) {
            objtext = objtext.concat(text[i]);
        }
        console.log(courseObjects);
    }

    console.log(courseObjects);

    return courseObjects;
};


const Education = () => {
    const [message, setMessage] = useState('');
    useEffect(() => { // eventually will have to change this to permanent server host endpoint
    fetch('http://localhost:3306/education')
      .then((res) => res.text())
      .then((data) => {
        console.log(data);
        setMessage(data);
      })
      .catch((err) => console.log(err));
  }, []);

    const courses2 = parseCourses(message);


    const courses = [{
        title: 'Seminar I',
        code: 'FS100',
        description: 'A class with assorted general readings'
    }, {
        title: 'Seminar II',
        code: 'FS101',
        description: 'A class with more assorted general readings'
    }, {
        title: 'Calculus 3',
        code: 'MATH200',
        description: 'Vectors and 3-dimensional integrals'
    }, {
        title: 'Introduction to Computing',
        code: 'CPSC110',
        description: 'The very first introductory class at UBC on computer programming.'
    }, {
        title: 'Video Game Programming',
        code: 'CPSC427',
        description: 'A class on game programming with a term-long group project to make a game.'
    }];



    return (
        <div 
            style={{
                fontFamily: 'serif'
            }}
        >
            {returnCourses(courses)}
        </div>
    );
    // TODO:
    // specify circle container in here
    // parse db response and display
};

export default Education;
