import React, {useEffect, useState} from 'react';
import './Card.css';


function frontToBack() {
    state.setFront(false);
}

function backToFront() {
    state.setFront(true);
}


const Card = (props) => {
    var [front, setFront] = useState({
        front: true
    });


    const handleClick = () => {

        front = !front;
    };

    let cardText;
    let button;

    if (front) {
        cardText = <b>{props.title}</b>;
        button = <button className="card-button" onClick={frontToBack}>LEARN MORE</button>;
    } else {
        cardText = <p>{props.info}</p>;
        button = <button className="card-button" onClick={backToFront}>CLOSE</button>;
    }

    return (
        <div className="card">
            <div style={{
            textAlign: "center",
            marginInline: "10%"
        }}>
                {cardText}
            </div>  
            <div style={{
            textAlign: "center"
        }}>
                {button}
            </div>

        </div>
    );
};

export default Card;
