import React, {useEffect, useState} from 'react';
import './Card.css';


function frontToBack() {
    this.setState({side: false});
}

function backToFront() {
    this.setState({side: true});
}


const Card = (props) => {
    var [front, setFront] = useState();

    const handleClick = () => {
        if (front == null) {
            front = true;
        }
        front = !front;
    };

    let cardText;
    let button;

    if (front) {
        cardText = <b>{props.title}</b>;
        button = (<button className="card-button" onClick={handleClick}>LEARN MORE</button>);
    } else {
        cardText = <p>{props.info}</p>;
        button = <button className="card-button" onClick={handleClick}>CLOSE</button>
    }

    return (
        <div className="card">
            <div style={{
            textAlign: "center",
            marginInline: "10%"
        }}>
                {cardText}
            </div>  
            <div style={{
            textAlign: "center"
        }}>
                {button};
            </div>

        </div>
    );
};

export default Card;
