import React from 'react';
import './Card.css';

var front;

function Flip() {
    front = !front;
    console.log(front);

};

function RenderCard(props) {
    if (front) {
        return props.title;
    } else {
        return props.info;
    }

}

function RenderButton() {
    if (front) {
        return "LEARN MORE";
    } else {
        return "CLOSE";
    }
}


const Card = (props) => {
    if (front == null) {
        front = true;
    }

    return (
        <div className="card">
            <div style={{
            textAlign: "center",
            marginInline: "10%"
        }}>
                <p>
                    {props.info}
                </p>
            </div>  
            <div style={{
            textAlign: "center"
        }}>
                <button className="card-button" onClick={Flip}>
                    <b>{RenderButton()}</b>
                </button>
            </div>

        </div>
    );
};

export default Card;
