import React from 'react';
import './Card.css';

var front;

function frontToBack() {
    this.setState({side: false});
}

function backToFront() {
    this.setState({side: true});
}


const Card = (props) => {
    const front = this.state.side; // true = front, false = back
    let cardText;
    let button;

    if (front) {
        cardText = <b>{props.title}</b>;
        button = (<button className="card-button" onClick={frontToBack}>LEARN MORE</button>);
    } else {
        cardText = <p>{props.info}</p>;
        button = <button className="card-button" onClick={backToFront}>CLOSE</button>
    }

    return (
        <div className="card">
            <div style={{
            textAlign: "center",
            marginInline: "10%"
        }}>
                {cardText}
            </div>  
            <div style={{
            textAlign: "center"
        }}>
                {button};
            </div>

        </div>
    );
};

export default Card;
