import React from 'react';
import './Card.css';

var front = true;

const Flip = () => {
    front = !front;
    console.log(front);
};


const Card = (props) => {
    if (front) {
        return (
            <div className="card">
                <div style={{
                textAlign: "center",
                marginInline: "10%"
            }}>
                    <h3>
                        {props.title}
                    </h3>
                </div>  
                <div style={{
                    textAlign: "center"
                }}>
                    <button className="card-button" onClick={Flip()}>
                        <b>LEARN MORE</b>
                    </button>
                </div>

            </div>
        );
    } else {
        return (
            <div className="card">
                <div style={{
                textAlign: "center",
                marginInline: "10%"
            }}>
                    <p>
                        {props.info}
                    </p>
                </div>  
                <div style={{
                textAlign: "center"
            }}>
                    <button className="card-button" onClick={Flip()}>
                        <b>CLOSE</b>
                    </button>
                </div>

            </div>
        );
    }
};

export default Card;
