import React from 'react';
import './Card.css';

var front;

const selectSide = (side) => {
    this.setState({ side });
}

function Flip() {
    front = !front;
    console.log(front);

};

const CardText = (props) => {
    if (front) {
        return (<b>{props.title}</b>);

    } else {
        return (<p>{props.info}</p>);
    }
};

const CardButtonLabel = () => {
    if (front) {
        return (<b>LEARN MORE</b>);
    } else {
        return (<b>CLOSE</b>);
    }
}


const Card = (props) => {
    if (front == null) {
        front = true;
    }

    return (
        <div className="card">
            <div style={{
            textAlign: "center",
            marginInline: "10%"
        }}>
                <CardText props={props} />
            </div>  
            <div style={{
            textAlign: "center"
        }}>
                <button className="card-button" onClick={selectSide}>
                    <CardButtonLabel/>
                </button>
            </div>

        </div>
    );
};

export default Card;
